allow:
Bash(powershell -Command "
$files = git diff --name-only --diff-filter=U
foreach \($file in $files\) {
    if \([string]::IsNullOrWhiteSpace\($file\)\) { continue }
    $content = Get-Content $file -Raw -Encoding UTF8
    # Replace conflict blocks: keep stashed \(after =======\), remove upstream \(before =======\)
    # Pattern: <<<<<<< Updated upstream ... ======= ... >>>>>>> Stashed changes
    while \($content -match ''\(?s\)<<<<<<< Updated upstream\\r?\\n\(.*?\)\\r?\\n=======\\r?\\n\(.*?\)\\r?\\n>>>>>>> Stashed changes''\) {
        $content = $content -replace ''\(?s\)<<<<<<< Updated upstream\\r?\\n.*?\\r?\\n=======\\r?\\n\(.*?\)\\r?\\n>>>>>>> Stashed changes'', ''$1''
    }
    [System.IO.File]::WriteAllText\($file, $content, [System.Text.UTF8Encoding]::new\($false\)\)
    Write-Host ""Resolved: $file""
}
")
